render_plane Graphics::create_text(const std::string& text, const std::map<uint8_t, render_plane>& table, const uint16_t spacing)
{
	std::string letter(1, text.at(0));
	render_plane rendered_text = table.at(Chars::KEY_MAP.at(letter));
	render_plane space = bitset_2d::create_plane(spacing, rendered_text.at(0).size(), false);
	for (size_t i = 1; i < text.length(); i++)
	{
		bitset_2d::append(rendered_text, space);

		letter = std::string(1, text.at(i));
		
		bitset_2d::append(rendered_text, table.at(Chars::KEY_MAP.at(letter)));
	}
	return rendered_text;
}